SELECT TOP 25 tbLCHEMICALS_MOVE.timeDate, tbLCHEMICALS_MOVE.timeHour, tblCHEMICALS.chemical, tblLINES.Line, tbLCHEMICALS_MOVE.volume, tbLCHEMICALS_MOVE.transferred, tbLCHEMICALS_MOVE.added, tbLCHEMICALS_MOVE.drained, tblLINES_1.Line, tblTECHNAME.firstName
FROM (((tbLCHEMICALS_MOVE LEFT JOIN tblCHEMICALS ON tbLCHEMICALS_MOVE.[chemical] = tblCHEMICALS.ID) LEFT JOIN tblLINES ON tbLCHEMICALS_MOVE.line = tblLINES.ID) LEFT JOIN tblTECHNAME ON tbLCHEMICALS_MOVE.techName = tblTECHNAME.ID) LEFT JOIN tblLINES AS tblLINES_1 ON tbLCHEMICALS_MOVE.movedLine = tblLINES_1.ID
ORDER BY tbLCHEMICALS_MOVE.timeDate DESC , tbLCHEMICALS_MOVE.timeHour DESC;
