SELECT TOP 15 tblMAINTENANCE_DAILY_CL.timeDate, tblMAINTENANCE_DAILY_CL.timeHour, tblMAINTENANCE_DAILY_CL.resistanceMeter, tblMAINTENANCE_DAILY_CL.waterMeter_CR, tblMAINTENANCE_DAILY_CL.waterMeter_CL, tblMAINTENANCE_DAILY_CL.uvLight, tblMAINTENANCE_DAILY_CL.cleanFloor, tblMAINTENANCE_DAILY_CL.usCleaned, tblMAINTENANCE_DAILY_CL.usRefilled, tblTECHNAME.firstName
FROM tblTECHNAME RIGHT JOIN tblMAINTENANCE_DAILY_CL ON tblTECHNAME.ID = tblMAINTENANCE_DAILY_CL.techName
ORDER BY tblMAINTENANCE_DAILY_CL.timeDate DESC , tblMAINTENANCE_DAILY_CL.timeHour DESC;
