SELECT tblDI_WATER.timeDate, tblDI_WATER.timeHour, tblLABORATORIES.nameLab, tblDI_WATER.resistivity, tblDI_WATER.toc, tblDI_WATER.dissolvedOxygen, tblDI_WATER.particles1, tblDI_WATER.particles2, tblDI_WATER.particles3, tblDI_WATER.particles4, tblDI_WATER.particles5, tblDI_WATER.bacteria, tblDI_WATER.silicaTotal, tblDI_WATER.silicaDissolved, tblDI_WATER.aluminum, tblDI_WATER.antimony, tblDI_WATER.arsenic, tblDI_WATER.barium, tblDI_WATER.boron, tblDI_WATER.cadmium, tblDI_WATER.calcium, tblDI_WATER.chromium, tblDI_WATER.copper, tblDI_WATER.iron, tblDI_WATER.lead, tblDI_WATER.lithium, tblDI_WATER.magnesium, tblDI_WATER.manganese, tblDI_WATER.nickel, tblDI_WATER.potassium, tblDI_WATER.sodium, tblDI_WATER.tin, tblDI_WATER.titanium, tblDI_WATER.vanadium, tblDI_WATER.zinc
