SELECT tblTITRATION.timeDate, tblCHEMICALS.chemical, tblLINES.Line, tblTITRATION.concNaOH, [concNaOH]*[volTitrant]/1000 AS moles, [moles]*1000/[volSample] AS concAcid, IIf([massSol]/[volSample]<>0,[massSol]/[volSample]) AS denAcid, ([temp]-32)*(5/9) AS cels, (999.8531+(0.0632693*[cels])+(-0.008523829*([cels]^2))+(0.00006943248*([cels]^3))+((-0.0000003821216)*([cels]^4)))/1000 AS denH2O, IIf([denacid]<>"" Or [denH2O]<>"",Round([denAcid]/[denH2O],5)) AS specGrav, [moles]*[tblCHEMICALS]![molecularWeight]/([tblCHEMICALS]![concentration]*[tblCHEMICALS]![density]*[volSample]) AS volPer, IIf([massSol]<>0,[moles]*[tblCHEMICALS]![molecularWeight]/[massSol]) AS wtPer
FROM ((tblTITRATION LEFT JOIN tblLINES ON tblTITRATION.line = tblLINES.ID) LEFT JOIN tblCHEMICALS ON tblTITRATION.chemical = tblCHEMICALS.ID) LEFT JOIN tblTECHNAME ON tblTITRATION.techName = tblTECHNAME.ID
WHERE (((tblTITRATION.timeDate)>=[TempVars]![tmpDate1] And (tblTITRATION.timeDate)<=[TempVars]![tmpDate2]) AND ((tblLINES.ID) Like [TempVars]![tmpLINE]) AND ((tblCHEMICALS.ID) Like [TempVars]![tmpChem]))
ORDER BY tblTITRATION.timeDate;
